import XLSX from 'xlsx';
import FileSaver from 'file-saver';

$(function(){
  var fixedCon = document.getElementsByClassName("el-table__fixed")[0];
  if(fixedCon){
    fixedCon.addEventListener("wheel", scrollDiv);
  }
});

Date.prototype.Format = function (fmt) { //author: meizz 
  var o = {
      "M+": this.getMonth() + 1, //月份 
      "d+": this.getDate(), //日 
      "h+": this.getHours(), //小时 
      "m+": this.getMinutes(), //分 
      "s+": this.getSeconds(), //秒 
      "q+": Math.floor((this.getMonth() + 3) / 3), //季度 
      "S": this.getMilliseconds() //毫秒 
  };
  if (/(y+)/.test(fmt)){
    fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
  }
  for (var k in o){
    if (new RegExp("(" + k + ")").test(fmt)) {
      fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));  
    }
  }
  
  return fmt;
}

function type(obj) {
  if (typeof(obj) == "object") {
    return obj.constructor;
  } else {
    return typeof(obj);
  }
}

// 去掉字符串中的数字
function trimNumber(str){ 
  return str.replace(/\d+/g,''); 
}

function getNum(text){
  var value = text.replace(/[^0-9]/ig,""); 
  return value;
}

function unique(array){ 
  var n = [array[0]]; //结果数组 
  //从第二项开始遍历 
  for(var i = 1; i < array.length; i++) { 
  //如果当前数组的第i项在当前数组中第一次出现的位置不是i， 
  //那么表示第i项是重复的，忽略掉。否则存入结果数组 
  if (array.indexOf(array[i]) == i) n.push(array[i]); 
  } 
  return n; 
}

//表格左侧fixed层添加scroll功能 start
function scrollDiv(e){
  if(e.wheelDelta < 0){  //向下滚动
    var scrollTop = $(".el-table__body-wrapper").scrollTop();
    $(".el-table__body-wrapper").scrollTop(scrollTop + 50);
  }else{ //向上滚动
    var scrollTop = $(".el-table__body-wrapper").scrollTop();
    $(".el-table__body-wrapper").scrollTop(scrollTop - 50);
  }
}

//返回汉字的拼音的第一个字母
function pinYin(st) {
  var checkCh = function (ch) {
    var uni = ch.charCodeAt(0);
    var strChineseFirstPY = '';
    //如果不在汉字处理范围之内,返回原字符
    if(uni > 40869 || uni < 19968)
      return ch;
    return strChineseFirstPY.charAt(uni-19968);
  }

  //返回汉字的拼音的第一个字母
  var getPY = function (str) {
    var ret = '';
    for(var i=0,len=str.length;i<len;i++)
    {
      var ch = str.charAt(i);
      ret += checkCh(ch);
    }
    return ret.toUpperCase();
  }


  return getPY(st);
}

function buildTreeByPID(recordList, idField, pidField, labelField){
  var result=[];
  var treeIndex={};
  recordList.forEach(function(item){
    var treeNode={};
    treeNode.label = item[labelField];
    treeNode.id = item[idField];
    treeNode.pid = item[pidField];
    treeNode.children = [];
    treeIndex[item[idField]]=treeNode;
  });
  recordList.forEach(function(item){    
    if(item[pidField]){
      treeIndex[item[pidField]].children.push(treeIndex[item[idField]]);
    }
    else{
      result.push(treeIndex[item[idField]]);
    }
  });
  return result;
}

function addBidNodeFields(root, bidNode, fieldList){
  var index = -1;  
  fieldList.forEach(function(item, idx){
    if(item.prop == root){
      index = idx;
    }
  });
  if(index==-1){
    return;
  }
  // 添加明细字段
  var lastParent=null;
  bidNode.forEach(function(record){
    var newField={label:record.description, description:record.description,width:80,childList:[]};    
    newField.prop = root + '_' + record.id;    
    if(record.pid==null){
      index = index + 1;
      fieldList.splice(index, 0, newField);      
      if(lastParent&&(lastParent.childList.length>0)){
        lastParent.childList.push({label:'合计',description:'合计',width:80,childList:[],prop:lastParent.prop});
      }
      lastParent = newField;
    }else if(lastParent){
      lastParent.childList.push(newField);      
    }
  });
  if(lastParent&&(lastParent.childList.length>0)){
    lastParent.childList.push({label:'合计',description:'合计',width:80,childList:[],prop:lastParent.prop});
  }          
}

function addBidNodeProjectSpecFields(bidNode, fieldList) {
  var bidNodeTree = []; 
  bidNode.forEach(function(item){
    var obj = {id: item.id, description:item.description, child:[]};
    if(item.pid==null){
      bidNodeTree.push(obj);
    }else if(bidNodeTree.length>0){
      bidNodeTree[bidNodeTree.length - 1].child.push(obj);
    }
  });
  var separator = '_';
  var projectSpec = 'attr' + separator;
  bidNodeTree.forEach(function(item){
    var newField = {label: item.description, width: 150, prop: projectSpec + item.id};
    fieldList.push(newField); 
    if(item.child.length>0){
      item.child.forEach(function(child){
        var newField = {label: child.description, width: 150, prop: projectSpec+item.id+separator+child.id};
        fieldList.push(newField); 
      });        
    }   
  });
}

function datenum(v, date1904) {
  if(date1904) v+=1462;
  var epoch = Date.parse(v);
  return (epoch - new Date(Date.UTC(1899, 11, 30))) / (24 * 60 * 60 * 1000);
}
 
function sheet_from_array_of_arrays(data, merges, opts) {
  var ws = {};
  var range = {s: {c:10000000, r:10000000}, e: {c:0, r:0 }};
  for(var R = 0; R != data.length; ++R) {
    for(var C = 0; C != data[R].length; ++C) {
      if(range.s.r > R) range.s.r = R;
      if(range.s.c > C) range.s.c = C;
      if(range.e.r < R) range.e.r = R;
      if(range.e.c < C) range.e.c = C;
      var cell = {v: data[R][C] };
      if(cell.v == null) continue;
      var cell_ref = XLSX.utils.encode_cell({c:C,r:R});
      
      if(typeof cell.v === 'number') cell.t = 'n';
      else if(typeof cell.v === 'boolean') cell.t = 'b';
      else if(cell.v instanceof Date) {
        cell.t = 'n'; cell.z = XLSX.SSF._table[14];
        cell.v = datenum(cell.v);
      }
      else cell.t = 's';
      
      ws[cell_ref] = cell;
    }
  }  
  if(range.s.c < 10000000){
    ws['!ref'] = XLSX.utils.encode_range(range);
  } 
  
  ws['!merges'] = merges;
  return ws;
}

function str2Buf(s) {
  if(typeof ArrayBuffer !== 'undefined') {
    var buf = new ArrayBuffer(s.length);
    var view = new Uint8Array(buf);
    for (var i=0; i!=s.length; ++i) view[i] = s.charCodeAt(i) & 0xFF;
    return buf;
  } else {
    var buf = new Array(s.length);
    for (var i=0; i!=s.length; ++i) buf[i] = s.charCodeAt(i) & 0xFF;
    return buf;
  }
}

function Workbook() {
  if(!(this instanceof Workbook)){
    return new Workbook();
  }
  this.SheetNames = [];
  this.Sheets = {};
}

function isArray(object){
    return object && typeof object==='object' &&
            Array == object.constructor;
}

function isInExcelHeader(config, key) {
  var isMulti = config.isMulti;
  var headerArray = ['A', 'B'];
  if (isMulti) {
    headerArray.push("C");
  }
  var is = false;
  for (var i = 0; i < headerArray.length; i ++) {
    if (headerArray[i] == key) {
      is = true;
      break;
    }
  }
  return is;
}

function writeExcel(data, merges, fileName, sheetName){
  var wb = new Workbook();
  var ws = sheet_from_array_of_arrays(data, merges);
  wb.SheetNames.push(sheetName);
  wb.Sheets[sheetName] = ws;
  var wbout = XLSX.write(wb, {bookType:'xlsx', bookSST:true, type: 'binary'});
  FileSaver.saveAs(new Blob([str2Buf(wbout)],{type:"application/octet-stream"}), fileName + ".xlsx");
} 

function readExcel(file, func){
  var reader = new FileReader();    
  reader.onload = function(e) {
    var data = e.target.result;           
    var workbook = XLSX.read(data, {type: 'binary'});
    if(func){
      func(workbook);                                 
    }
  };
  reader.readAsBinaryString(file);
}


function toDecimal(value){
  if(isNaN(value)){
    return 0;
  }
  return Math.round(value * 100) / 100;    
}

function number2ABC(number){
  var letterDict = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  var result = '';
  var n = number + 1;
  var i = Math.round(n/27);
  var j = n - i*26;
  if(i>0){
    result = result + letterDict.charAt(i-1);
  }
  if(j>0){
    result = result + letterDict.charAt(j-1);  
  }  
  return result;
}

function nextSibling(list, index){
  var result = index + 1;
  var level = list[index].level;
  while(result<list.length&&list[result].level>level){
    result=result + 1;
  }
  return result;
}


export default function commJs(){
  return {
    buildTreeByPID: buildTreeByPID,
    pinYin: pinYin,
    addBidNodeFields: addBidNodeFields,
    unique: unique,
    type: type,
    trimNumber: trimNumber,
    readExcel: readExcel,
    writeExcel: writeExcel,
    number2ABC: number2ABC,
    getNum: getNum,
    addBidNodeProjectSpecFields: addBidNodeProjectSpecFields,
    toDecimal: toDecimal,
    nextSibling: nextSibling,
    isInExcelHeader: isInExcelHeader
  }
}
